package sc.small.chat.global.config;import com.querydsl.jpa.impl.JPAQuery;import com.querydsl.jpa.impl.JPAQueryFactory;import jakarta.persistence.EntityManager;import jakarta.persistence.PersistenceContext;import jakarta.persistence.Tuple;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class QueryDSLConfiguration {    @PersistenceContext    private EntityManager em;    @Bean    public JPAQueryFactory jpaQueryFactory() {        return new JPAQueryFactory(em);    }    @Bean    public JPAQuery<Tuple> jpaQuery() {        return new JPAQuery<>(em);    }}