package sc.small.chat.global.config;import lombok.RequiredArgsConstructor;import org.springframework.boot.autoconfigure.data.redis.RedisProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.data.redis.connection.RedisConnectionFactory;import org.springframework.data.redis.connection.lettuce.LettuceConnectionFactory;import org.springframework.data.redis.core.RedisTemplate;import org.springframework.data.redis.serializer.GenericJackson2JsonRedisSerializer;import org.springframework.data.redis.serializer.StringRedisSerializer;import sc.small.chat.global.dto.TokenInfoDTO;@RequiredArgsConstructor@Configurationpublic class RedisConfiguration {    private final RedisProperties redisProperties;    @Bean    public RedisConnectionFactory redisConnectionFactory() {        return new LettuceConnectionFactory(redisProperties.getHost(), redisProperties.getPort());    }    @Bean    public RedisTemplate<String, TokenInfoDTO> redisTemplate(RedisConnectionFactory redisConnectionFactory) {        RedisTemplate<String, TokenInfoDTO> redisTemplate = new RedisTemplate<>();        redisTemplate.setConnectionFactory(redisConnectionFactory);        redisTemplate.setKeySerializer(new StringRedisSerializer());        redisTemplate.setValueSerializer(new GenericJackson2JsonRedisSerializer());        return redisTemplate;    }}