package sc.small.chat.global.exception;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.AllArgsConstructor;import lombok.Getter;import org.springframework.http.HttpStatus;@Getter@AllArgsConstructorpublic class SmallChatHttpException extends RuntimeException{    @JsonIgnore    private HttpStatus httpStatus;    private int code;    private String message;    public SmallChatHttpException(HttpStatus httpStatus, String string) {        this.httpStatus = httpStatus != null ? httpStatus : HttpStatus.BAD_REQUEST;        this.code = this.httpStatus.value();        this.message = message;    }    public void SmallChatHttpException(HttpStatus httpStatus, String message) {        this.httpStatus = httpStatus != null ? httpStatus : HttpStatus.BAD_REQUEST;        this.code = this.httpStatus.value();        this.message = message;    }}