package sc.small.chat.global.security.oauth.enums;import lombok.Getter;@Getterpublic enum ProviderType {    GOOGLE("구글", "GOOGLE"),    KAKAO("카카오", "KAKAO"),    NAVER("네이버", "NAVER");    private final String displayName;    private final String type;    ProviderType(String displayName, String type) {        this.displayName = displayName;        this.type = type;    }    public static ProviderType typeOf(String type) {        for (ProviderType resultReturn : ProviderType.values()) {            if (resultReturn.getType().equals(type)) {                return resultReturn;            }        }        return null;    }    public static String typeOfDisplayName(String type) {        ProviderType resultReturn = typeOf(type);        return (resultReturn != null) ? resultReturn.getDisplayName() : type;    }}