package sc.small.chat.global.util;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;public class ClosePersonalityUtil {    private static final Map<String, List<String>> adjacencyMap = new HashMap<>();    static {        adjacencyMap.put("A", List.of("A", "B", "H"));        adjacencyMap.put("B", List.of("B", "C", "F"));        adjacencyMap.put("C", List.of("C", "E", "B"));        adjacencyMap.put("D", List.of("D", "G", "E"));        adjacencyMap.put("E", List.of("E", "F", "C"));        adjacencyMap.put("F", List.of("F", "H", "D"));        adjacencyMap.put("G", List.of("G", "D", "A"));        adjacencyMap.put("H", List.of("H", "A", "G"));    }    public static boolean isClosePersonality(String mine, String theOther) {        return adjacencyMap.getOrDefault(mine, List.of()).contains(theOther);    }    public static List<String> findClosestPersonalities(String personality, List<String> personalities) {        List<String> closePersonalities = adjacencyMap.getOrDefault(personality, List.of());        List<String> matchedPersonalities = new ArrayList<>();        for (String p : personalities) {            if (closePersonalities.contains(p)) {                matchedPersonalities.add(p);            }        }        return matchedPersonalities;    }}