package sc.small.chat.member.controller;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.tags.Tag;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import sc.small.chat.global.annotaion.ValidateDTO;import sc.small.chat.member.dto.SignUpDTO;import sc.small.chat.member.service.MemberService;@Tag(name = "Member 회원")@Slf4j@RestController@RequiredArgsConstructor@RequestMapping("/members")public class MemberController {    private final MemberService memberService;    @Operation(summary = "회원가입", description = "회원가입 API")    @PostMapping("/sign-up")    public SignUpDTO.Response signUp(@RequestBody @ValidateDTO SignUpDTO.Request signUpRequest) {        return memberService.signUp(signUpRequest);    }}