package sc.small.chat.member.entity;import jakarta.persistence.CascadeType;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.OneToMany;import jakarta.persistence.Table;import java.util.ArrayList;import java.util.List;import lombok.Getter;import lombok.NoArgsConstructor;import sc.small.chat.oauth.entity.OAuthProfile;import sc.small.chat.personality.entity.Personality;@Entity@Table(name = "members")@Getter@NoArgsConstructorpublic class Member {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "daily_small_chat_count")    private int dailySmallChatCount;    @OneToMany(mappedBy = "member", cascade = CascadeType.ALL)    private List<OAuthProfile> oAuthProfiles = new ArrayList<>();    @OneToMany(mappedBy = "member", cascade = CascadeType.ALL)    private List<Personality> personalities = new ArrayList<>();}