package sc.small.chat.personality.controller;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.tags.Tag;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import sc.small.chat.global.annotaion.ValidateDTO;import sc.small.chat.personality.dto.OnBoardingDTO;import sc.small.chat.personality.service.PersonalityService;@Tag(name = "Personality 성향")@Slf4j@RestController@RequiredArgsConstructor@RequestMapping("/personalities")public class PersonalityController {    private final PersonalityService personalityService;    @Operation(summary = "온보딩 프로세스", description = "회원 성향(personality) 설정 및 저장")    @PostMapping("/on-boarding")    public OnBoardingDTO.Response onBoarding(@RequestBody @ValidateDTO OnBoardingDTO.Request onBoardingRequest) {        return personalityService.savePersonalityWithMember(onBoardingRequest);    }}