package sc.small.chat.personality.dto;import io.swagger.v3.oas.annotations.media.Schema;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Pattern;@Schema(description = "온보딩 프로세스 DTO", name = "OnBoardingDTO")public class OnBoardingDTO {    @Schema(description = "OnBoardingRequestDTO")    public record Request(        @NotNull        @Pattern(regexp = "[A-H]", message = "The field must be a letter from A to H.")        @Schema(description = "code", type = "String", example = "C")        String personalityType    ) {}    @Schema(description = "OnBoardingResponseDTO")    public record Response(        @Schema(description = "클라이언트 저장용 성향 ID", type = "Long", example = "1")        Long personalityId,        @Schema(description = "성향 한글 이름", type = "String", example = "아이디어뱅크 유형")        String koreaDisplayName,        @Schema(description = "성향 영문 이름", type = "String", example = "IDEA_GENERATOR")        String englishDisplayName    ) {}}