package sc.small.chat.personality.enums;import lombok.Getter;@Getterpublic enum PersonalityType {    IDEA_GENERATOR("A", "아이디어뱅크 유형", "Idea Generator"),    STRATEGIC_THINKER("B", "전략가 유형", "Strategic Thinker"),    LOGICAL_ANALYZER("C", "논리적 분석가 유형", "Logical Analyzer"),    TEAM_PLAYER("D", "팀 플레이어 유형", "Team Player"),    FOCUSED_ACHIEVER("E", "집중력 대장 유형", "Focused Achiever"),    CONTINUOUS_LEARNER("F", "꾸준한 학습자 유형", "Continuous Learner"),    COMMUNITY_BUILDER("G", "커뮤니티 활동가 유형", "Community Builder"),    EXECUTION_MASTER("H", "실행력 마스터 유형", "Execution Master");    private final String type;    private final String koreaDisplayName;    private final String englishDisplayName;    PersonalityType(String type, String displayKoreaName, String displayEnglishName) {        this.type = type;        this.koreaDisplayName = displayKoreaName;        this.englishDisplayName = displayEnglishName;    }    public static PersonalityType typeOf(String type) {        for (PersonalityType resultReturn : PersonalityType.values()) {            if (resultReturn.getType().equals(type)) {                return resultReturn;            }        }        return null;    }    public static String typeOfKoreaDisplayName(String type) {        PersonalityType resultReturn = typeOf(type);        return (resultReturn != null) ? resultReturn.getKoreaDisplayName() : type;    }    public static String typeOfEnglishDisplayName(String type) {        PersonalityType resultReturn = typeOf(type);        return (resultReturn != null) ? resultReturn.getEnglishDisplayName() : type;    }}