package sc.small.chat.personality.service;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import sc.small.chat.personality.dto.OnBoardingDTO;import sc.small.chat.member.repository.MemberRepository;import sc.small.chat.personality.entity.Personality;import sc.small.chat.personality.enums.PersonalityType;import sc.small.chat.personality.reposirtory.PersonalityRepository;@Slf4j@Service@RequiredArgsConstructorpublic class PersonalityService {    private final MemberRepository memberRepository;    private final PersonalityRepository personalityRepository;    @Transactional    public OnBoardingDTO.Response savePersonality(OnBoardingDTO.Request onBoardingRequest) {        Personality personality = Personality.builder()            .personalityType(PersonalityType.typeOf(onBoardingRequest.personalityType()))            .build();        personalityRepository.save(personality);        return new OnBoardingDTO.Response(            personality.getId(),            personality.getPersonalityType().getKoreaDisplayName(),            personality.getPersonalityType().getEnglishDisplayName()        );    }}