package sc.small.chat.profile.entiy;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.ManyToOne;import jakarta.persistence.Table;import java.sql.Timestamp;import lombok.Getter;import lombok.NoArgsConstructor;import org.hibernate.annotations.SQLDelete;import org.hibernate.annotations.SQLRestriction;import sc.small.chat.global.entity.BaseEntity;import sc.small.chat.profile.enums.CareerType;@Entity@Table(name = "careers")@Getter@NoArgsConstructor@SQLRestriction("deleted_at IS NULL")@SQLDelete(sql = "UPDATE careers SET deleted_at = NOW() WHERE id = ?")public class Career extends BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "company_name")    private String companyName;    @Column(name = "position")    private String position;    @Column(name = "career_started_at")    private Timestamp careerStartedAt;    @Column(name = "career_ended_at")    private Timestamp careerEndedAt;    @Column(name = "career_type")    private CareerType careerType;    @ManyToOne    @JoinColumn(name = "profile_id", nullable = false)    private Profile profile;}