package sc.small.chat.profile.entiy;import jakarta.persistence.CascadeType;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.ManyToOne;import jakarta.persistence.OneToMany;import jakarta.persistence.OneToOne;import jakarta.persistence.Table;import java.util.HashSet;import java.util.Set;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import org.hibernate.annotations.SQLDelete;import org.hibernate.annotations.SQLRestriction;import sc.small.chat.global.entity.BaseEntity;import sc.small.chat.member.entity.Member;@Entity@Table(name = "profiles")@Getter@NoArgsConstructor@SQLRestriction("deleted_at IS NULL")@SQLDelete(sql = "UPDATE profiles SET deleted_at = NOW() WHERE id = ?")public class Profile extends BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "nickname")    private String nickname;    @Column(name = "years")    private int years;    @Column(name = "is_open")    private boolean isOpen = true;    @OneToOne(mappedBy = "profile")    private Member member;    @OneToMany(mappedBy = "profile", cascade = CascadeType.ALL)    private Set<ProfileJob> profileJobs = new HashSet<>();    @OneToMany(mappedBy = "profile", cascade = CascadeType.ALL)    private Set<Career> careers = new HashSet<>();    @Builder    public Profile(String nickname, int years) {        this.nickname = nickname;        this.years = years;    }}