package sc.small.chat.profile.entiy;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.ManyToOne;import jakarta.persistence.Table;import lombok.Getter;import lombok.NoArgsConstructor;import org.hibernate.annotations.SQLDelete;import org.hibernate.annotations.SQLRestriction;import sc.small.chat.global.entity.BaseEntity;@Entity@Table(name = "topics")@Getter@NoArgsConstructor@SQLRestriction("deleted_at IS NULL")@SQLDelete(sql = "UPDATE topics SET deleted_at = NOW() WHERE id = ?")public class Topic extends BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "description")    private String description;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "profile_id")    private Profile profile;    public void setProfile(Profile profile) {        this.profile = profile;        profile.getTopics().add(this);    }}