package sc.small.chat.profile.enums;import lombok.Getter;@Getterpublic enum CareerType {    FULL_TIME( "정규직"),    CONTRACT("계약직"),    INTERN("인턴");    private final String type;    CareerType(String type) {        this.type = type;    }    public static CareerType typeOf(String type) {        for (CareerType resultReturn : CareerType.values()) {            if (resultReturn.getType().equals(type)) {                return resultReturn;            }        }        return null;    }}