package sc.small.chat.profile.enums;import lombok.Getter;@Getterpublic enum CareerType {    FULL_TIME("full_time", "정규직"),    CONTRACT("contract","계약직"),    INTERN("intern","인턴");    private final String displayName;    private final String type;    CareerType(String displayName, String type) {        this.displayName = displayName;        this.type = type;    }    public static CareerType typeOf(String type) {        for (CareerType resultReturn : CareerType.values()) {            if (resultReturn.getType().equals(type)) {                return resultReturn;            }        }        return null;    }    public static String typeOfDisplayName(String type) {        CareerType resultReturn = typeOf(type);        return (resultReturn != null) ? resultReturn.getDisplayName() : type;    }}