package sc.small.chat.profile.enums;import lombok.Getter;import sc.small.chat.oauth.enums.ProviderType;@Getterpublic enum YearsType {    ALL("전체", "ALL"),    JUNIOR("주니어", "JUNIOR"),    MIDDLE("미들", "MIDDLE"),    SENIOR("시니어", "SENIOR");    private final String displayName;    private final String type;    YearsType(String displayName, String type) {        this.displayName = displayName;        this.type = type;    }    public static YearsType typeOf(String type) {        for (YearsType resultReturn : YearsType.values()) {            if (resultReturn.getType().equals(type)) {                return resultReturn;            }        }        return null;    }    public static String typeOfDisplayName(String type) {        YearsType resultReturn = typeOf(type);        return (resultReturn != null) ? resultReturn.getDisplayName() : type;    }}