package sc.small.chat.profile.service;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import sc.small.chat.member.dto.SignUpDTO.SignUpCareerRequestDTO;import sc.small.chat.profile.entiy.Career;import sc.small.chat.profile.entiy.Profile;import sc.small.chat.profile.repository.CareerRepository;@Slf4j@Service@RequiredArgsConstructorpublic class CareerService {    private final CareerRepository careerRepository;    @Transactional    public Career saveForSignUp(SignUpCareerRequestDTO signUpCareerRequestDTO, Profile profile) {        Career career = signUpCareerRequestDTO.toEntity();        career.setProfile(profile);        return careerRepository.save(career);    }}