package sc.small.chat.profile.service;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import sc.small.chat.member.dto.SignUpDTO.SignUpJobRequestDTO;import sc.small.chat.profile.entiy.Job;import sc.small.chat.profile.repository.JobRepository;@Slf4j@Service@RequiredArgsConstructorpublic class JobService {    private final JobRepository jobRepository;    @Transactional    public Job saveForSignUp(SignUpJobRequestDTO signUpJobRequestDTO) {        return jobRepository.save(signUpJobRequestDTO.toEntity());    }}