package sc.small.chat.profile.service;import java.util.List;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import sc.small.chat.profile.entiy.ProfileJob;import sc.small.chat.profile.repository.ProfileJobRepository;@Slf4j@Service@RequiredArgsConstructorpublic class ProfileJobService {    private final ProfileJobRepository profileJobRepository;    @Transactional    public void saveAll(List<ProfileJob> profileJobs) {        profileJobRepository.saveAll(profileJobs);    }    @Transactional    public void deleteAll(List<ProfileJob> profileJobs) {        profileJobRepository.deleteAll(profileJobs);    }}